mapper.select:

select no, userid, subject, replace(content, '\n', '<br>') content, regtime
from notice



		where subject like concat('%', #{word}, '%')


		where ${key} = #{word}


order by no desc
limit #{start}, #{spp}

select savefolder, originfile, savefile
from fileinfo
where noticeno = #{no}

select count(no) from notice
where subject like concat('%', #{word}, '%')
where ${key} = #{word}

select no, userid, subject, content, regtime
from notice
where no = #{no}

mapper.update:
update notice
set subject = #{subject}, content = #{content}
where no = #{no}
