mapper.select:

select noticeno, userid, subject, replace(content, '\n', '<br>') content, regtime
from notice



		where subject like concat('%', #{word}, '%')


		where ${key} = #{word}


order by noticeno desc
limit #{start}, #{spp}

select savefolder, originfile, savefile
from file_info
where noticeno = #{noticeno}

select count(noticeno) from notice
where subject like concat('%', #{word}, '%')
where ${key} = #{word}

select noticeno, userid, subject, content, regtime
from notice
where noticeno = #{noticeno}

mapper.update:
update notice
set subject = #{subject}, content = #{content}
where noticeno = #{noticeno}
